import React, { Component } from 'react';
import test from '../data/test.json'

 class SubThreadDisplay extends Component {
    constructor(props) {
        super(props);
        this.state = {
            PageAmount: 0,
            NextPage: 0,
            Topics  : []

        };

    }

    componentDidMount() {
        let Topic = test.Topics.map((Topic) => {
                const newTopic  = {'id': Topic.id, 'title': Topic.Title, 'view': Topic.View, 'ds': Topic.DateStart };
                const newArray = this.state.Topics.slice();
                newArray.push(newTopic);
                this.setState({ Topics: newArray });
        })
        this.setState({PageAmount : test.PageAmount,
                    NextPage : test.NextPage});
    }
        render() {
            return (
                <ul>
                  {
                    this.state.Topics.map(function(rowdata,index) {
                            return ( <h1> {rowdata.Id} </h1>
                            )
                        }
                    )
                  }
            </ul>
            );
        }

}

export default SubThreadDisplay;
