import React, { Component } from 'react';
import Topic from 'Topic'
import test from '../data/test.json'

 class SubThreadDisplay extends Component {
    constructor(props) {
        super(props);
        this.state = {
            PageAmount: 0,
            NextPage: 0,
            Topics  : []

        };

    }

    componentDidMount() {
        let test = require('../data/test.json')
        let Topics = test.Topics.map((Topics, index) => {
        return (
        <Topic
          id={Topics.Id}
          title={Topics.Title}
          view={Topics.View}
          ds={Topics.DateStart}
        />
      )
    })
        this.setState({PageAmount : test.PageAmount,
                    NextPage : test.NextPage,
                    Topics: Topics});
    }
        render() {
            return (
                <ul>
                        {this.state.Topics}
                </ul>
            );
        }

}

export default SubThreadDisplay;
